mapper.update:
      UPDATE commodity SET commodityTypeID=#{commodityTypeID,jdbcType=INTEGER},
			             uOMID=#{uOMID,jdbcType=INTEGER},
			             commodityName=#{commodityName,jdbcType=VARCHAR},
			             salePrice=#{salePrice,jdbcType=DOUBLE}
                   WHERE id = #{id,jdbcType=INTEGER}

mapper.select:
  select c.*,a.attributeDetailsName as commodityTypeName,b.attributeDetailsName as uOMName 
from commodity c INNER JOIN attributedetails a on c.commodityTypeID=a.id 
INNER JOIN attributedetails b on c.uOMID =b.id   
WHERE commodityName like CONCAT('%',#{commodityName,jdbcType=VARCHAR},'%') 
and commodityTypeID=#{commodityTypeID,jdbcType=INTEGER}
limit #{start},#{pageSize}
